import os, json
versions_dir = "notebooks/versions"
rows = []
if os.path.isdir(versions_dir):
    for name in sorted(os.listdir(versions_dir)):
        if name.endswith(".ipynb") and "_checkpoint" not in name:
            meta = os.path.join(versions_dir, name + ".meta.json")
            message = ""
            if os.path.exists(meta):
                try:
                    with open(meta, "r", encoding="utf-8") as f:
                        message = json.load(f).get("message", "")
                except Exception:
                    pass
            rows.append((name, message))

if rows:
    print("| Snapshot | Kommentar |")
    print("|---|---|")
    for name, msg in rows:
        print(f"| [{name}]({versions_dir}/{name}) | {msg} |")
else:
    print("_Noch keine Snapshots gespeichert._")


